	// phone filter
	public static List<HotelDTO> filterList(List<HotelDTO> list) throws Exception{

		System.out.println("filterList 메서드에 도착했습니다.");

		List<HotelDTO> hotelList = list;

		List<HotelDTO> totalList = new ArrayList<>();


		for(int i = 0; i < hotelList.size(); i++) {

			// 1. 전화번호, 주소 포함 여부 확인
			System.out.println("정보 없는 호텔 리스트에서 삭제 중");
			if((hotelList.get(i).getHotelPhone().length() < 5) || (hotelList.get(i).getHotelRoadAddress().length() < 5)) {
				hotelList.remove(i);
				continue;
			}

			// 2. 전화번호 중복 확인
			System.out.println("전화번호 중복 확인 중");
			for(int k = 1; k < i; k++) {
				if(hotelList.get(i).getHotelPhone().equals(hotelList.get(k).getHotelPhone())) {
					hotelList.remove(i);
					continue;
				}
			}

			// 3. 이름 필터링
			System.out.println("호텔 이름 중복 확인 중");
			if(nameTest(hotelList.get(i))) {
				System.out.println(hotelList.get(i).getHotelName());
				totalList.add(hotelList.get(i));
			}
		}
		return totalList;
	}

