	public static List<HotelDTO> jsonParsing(String inputLine) {
		
		System.out.println("inputLine을 받아 parsing하는 중......");
		
		List<HotelDTO> list = new ArrayList<>();

		String jsonString = inputLine.toString();

		JSONObject jObject = new JSONObject(jsonString);

		JSONArray jArray = jObject.getJSONArray("documents");

		for(int i = 0; i < jArray.length(); i++) {

			JSONObject obj = jArray.getJSONObject(i);

			String hotelId = obj.getString("id");
			String hotelName = obj.getString("place_name");
			String hotelInfo =  getTargetInfo(hotelId);
			String hotelPhone = obj.getString("phone");
			String hotelRoadAddress = obj.getString("road_address_name");
			String hotelLongitude = obj.getString("x");
			String hotelLatitude = obj.getString("y");
			String hotelUrl = obj.getString("place_url");
			String categoryName = obj.getString("category_name");
			String hotelScore = String.format("%.1f", 3 + Math.random()*2);
			String hotelDetail = getTargetDetail(hotelId);
			
			// 중복검사
			HotelDTO dto = new HotelDTO(hotelId, hotelName, hotelInfo, hotelPhone, hotelRoadAddress, hotelLongitude, hotelLatitude, hotelScore, hotelDetail);
			System.out.println(dto.getHotelId() + " parsing 및 dto 객체 생성 완료");
			list.add(dto);
		}

		return list;
	}
	
